import React from 'react';
import './App.css';
import Todolist from "./components/Todolist";

function App() {
    const task1 = [
        {id: 1, title: 'HTML&CSS', isDone: true},
        {id: 2, title: 'React', isDone: false},
        {id: 3, title: 'TypeScript', isDone: true}
    ]

    const removeTask = (id: number) => {
        task1 = task1.filter(task => task.id =! id)
        console.log(task1)
    }

    return (
        <div className="App">
            <Todolist title={'New York'} tasks={task1}/>
            <Todolist title={'Chicago'} tasks={task1}/>
        </div>
    );
}

export default App;
